<?php $lang = Yii::app()->language; ?>
<div class="phrase<?php echo ($data->status != PhraseTranslation::STATUS_NEW_PENDING ? ' pending' : ''); ?>"><span class="info"><span class="from-lang"><?php echo $data->phrase->language->$lang . '-'; ?></span><span class="to-lang"><?php echo $data->translationPhrase->language->$lang; ?></span><span class="from-phrase"><?php echo $data->phrase->phrase; ?></span><span class="to-phrase"><?php echo $data->translationPhrase->phrase; ?></span></span><span class="meta"><?php echo $data->user->first_name . ' ' . $data->user->last_name . CHtml::tag('div', array('class'=>'date'), ContentModule::getFormattedRelativeDate($data->date)); ?></span></div>